package com.lhj.sep10.jstlcore;

public class Snack {
	private String name;
	private int price;
	
	public Snack() {
		// TODO Auto-generated constructor stub
	}

	public Snack(String name, int price) {
		super();
		this.name = name;
		this.price = price;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getPrice() {
		return price;
	}

	public void setPrice(int price) {
		this.price = price;
	}
	
	
}
